import { useState } from "react";
import styled from 'styled-components';
import Header from "../../components/Header";

const PagBase = styled.section`
pagBase {
  display: flex;
}

.menu-lateral {
  height: 100vh;
  width: 20vw;
}

.menu-lateral ul {
  padding: 20px;
  border-top: 1px solid black;
  list-style: none;
}

.menu-lateral h2 {
  padding-left: 20px;
  margin-bottom: 22px;
}

.gerenciador-de-tarefas {
  width: 100vw;
  border-left: 1px solid black;
}

.minhas-tarefas {
  width: 100vw;
  border-left: 1px solid black;
}

.filtro-menu-lateral {
  background: none;
  border: none;
  padding: 8px 16px;
  cursor: pointer;
  font-weight: bold;
  color: #888;
  border-bottom: 2px solid transparent;
  transition: 0.3s;
}

.filtro-menu-lateral:hover {
  color: #000;
}

.filtro-menu-lateral.ativo {
  color: #000;
  background-color: #f0f0f0;
  border-radius: 5px;
}

ul li img {
  width: 25px;
  margin-right: 15px;
  margin-bottom: -7px;
}

h2{
  margin-left:16px ;
}

h2 img {
  width: 25px;
  margin-right: 15px;
  margin-bottom: -6px;
}
h1{
 font-size: 35px;
}
`;

function PaginaBase() {
      const [filtro, setFiltro] = useState("dashboard");
    const [pagina, setPagina] = useState("dashboard");
    const [icone, setIcone] = useState("dashboard");
    const [titulo, setTitulo] = useState("Dashboard");

    const paginas = {
        dashboard: <DashBoard setPagina={setPagina} setIcone={setIcone} setTitulo={setTitulo} setFiltro={setFiltro} />,
        tarefas: <Tarefas />,
        perfil: <Perfil />
    }


  return (
    <PagBase>
      <section className="menu-lateral">
        <h2>menu lateral</h2>
        <ul>
          <li>Agenda</li>
          <li>Clientes</li>
          <li>Servicos</li>
          <li>Configurações</li>
        </ul>
      </section>
      <div className='minhas-tarefas'>
                <main>
                    <Header
                        titulo={titulo}
                        setPagina={setPagina}
                        setTitulo={setTitulo}
                        setFiltro={setFiltro}
                        setIcone={setIcone} />
                    {paginas[pagina]}
                </main>
            </div>
    </PagBase>
  );
}

export default PaginaBase;
