import styled from 'styled-components';

// Container principal do header
const HeaderContainer = styled.header`
  display: flex;
  justify-content: space-between;
  align-items: center;
  border-bottom: 1px solid black;
  padding: 0 30px;
  background-color: ${(props) => (props.theme === 'escuro' ? '#1b1b1b' : '#fff')};
  color: ${(props) => (props.theme === 'escuro' ? '#fff' : '#000')};
  box-shadow: 0 1px 5px rgba(0, 0, 0, 0.1);

  h2 {
    font-size: 1.5rem;
    margin: 20px 0;
  }
`;

// Grupo de ícones à direita
const IconGroup = styled.div`
  display: flex;
  gap: 16px;
  align-items: center;
  width: 120px;
  justify-content: flex-end;
`;

// Estilização da imagem de tema
const ThemeIcon = styled.img`
  width: 30px;
  height: 30px;
  cursor: pointer;
  border-radius: 50%;

  &:hover {
    opacity: 0.8;
  }
`;

// Estilização da foto do perfil
const ProfilePhoto = styled.img`
  height: 30px;
  width: 30px;
  border-radius: 50%;
  cursor: pointer;
  border: 2px solid ${(props) => (props.theme === 'escuro' ? '#555' : '#ddd')};

  &:hover {
    border-color: #007bff;
    transition: border-color 0.2s;
  }
`;

function Header({ titulo, setPagina, setTitulo, setFiltro, setIcone }) {

  const paginaPerfil = () => {
    setPagina('perfil');
    setTitulo('Perfil');
    setFiltro('perfil');
    setIcone('perfil');
  };

