import { useState } from "react";
import styled from 'styled-components';
import Header from "../../components/Header";
import DashBoard from "../Dashboard";
import Clientes from "../Clientes";

// Se você tiver tema escuro, descomente e use conforme necessário
// import iconeDashboardEscuro from '../../assets/imagens/dashboard-escuro.png';
// ... etc

import { useTema } from "../../components/ThemeContext"; // Descomente se usar tema

const PagBase = styled.section`
  display: flex;
  height: 100vh;

  .menu-lateral {
    height: 100%;
    width: 20vw;
    background-color: #fff;
    box-shadow: 2px 0 5px rgba(0, 0, 0, 0.1);
  }

  .menu-lateral h2 {
    display: flex;
    align-items: center;
    padding: 20px 0;
    margin-bottom: 22px;
    font-size: 1.5rem;
    color: #333;
  }

  .menu-lateral h2 img.logo {
    width: 180px;
    margin-left: 15px;
    margin-bottom: 0;
  }

  .menu-lateral ul {
    padding: 0;
    margin: 0;
    list-style: none;
  }

  .menu-lateral ul li {
    padding: 0;
    margin: 0;
  }

  .filtro-menu-lateral {
    display: flex;
    align-items: center;
    width: 100%;
    background: none;
    border: none;
    padding: 12px 20px;
    cursor: pointer;
    font-weight: 500;
    color: #888;
    border-bottom: 1px solid #eee;
    transition: all 0.3s ease;
    font-size: 1rem;
  }

  .filtro-menu-lateral:hover,
  .filtro-menu-lateral.ativo {
    color: #000;
    background-color: #f0f0f0;
    border-radius: 8px;
    margin: 0 8px;
    padding: 12px 20px;
  }

  .filtro-menu-lateral img {
    width: 24px;
    margin-right: 15px;
    opacity: 0.8;
  }

  .minhas-tarefas {
    flex: 1;
    border-left: 1px solid #ddd;
    overflow-y: auto;
    background-color: #f9f9f9;
  }
`;

function PaginaBase() {
  const [filtro, setFiltro] = useState("dashboard");
  const [pagina, setPagina] = useState("dashboard");
  const [icone, setIcone] = useState("dashboard");
  const [titulo, setTitulo] = useState("Dashboard");

  // Se você usa tema claro/escuro
  const { tema } = useTema(); // Remova se não usar

  // Mapeamento de ícones (ajuste os caminhos conforme sua pasta)
  const icones = {
    dashboard: iconeDashboard, // tema === "claro" ? iconeDashboard : iconeDashboardEscuro
    agenda: iconeAgenda,
    clientes: iconeClientes,
    servicos: iconeServicos,
    configuracoes: iconeConfig,
  };

  // Mapeamento de páginas
  const paginas = {
    dashboard: <DashBoard setPagina={setPagina} setTitulo={setTitulo} setFiltro={setFiltro} setIcone={setIcone} />,
    clientes: <Clientes />,
    // Adicione outras páginas conforme necessário
    // agenda: <Agenda />,
    // servicos: <Servicos />,
  };

  // Função para atualizar estado ao clicar
  const handleMenuClick = (paginaKey, tituloMenu, iconeKey) => {
    setPagina(paginaKey);
    setTitulo(tituloMenu);
    setFiltro(paginaKey);
    setIcone(iconeKey);
  };

  return (
    <PagBase>
      <section className="menu-lateral">
        <h2>
          <img src={icones[icone]} alt="" className="icone-topo" />
          {titulo}
        </h2>
        <ul>
          <li>
            <button
              className={`filtro-menu-lateral ${filtro === "dashboard" ? "ativo" : ""}`}
              onClick={() => handleMenuClick("dashboard", "Dashboard", "dashboard")}
            >
              <img src={icones.dashboard} alt="" /> Dashboard
            </button>
          </li>
          <li>
            <button
              className={`filtro-menu-lateral ${filtro === "agenda" ? "ativo" : ""}`}
              onClick={() => handleMenuClick("agenda", "Agenda", "agenda")}
            >
              <img src={icones.agenda} alt="" /> Agenda
            </button>
          </li>
          <li>
            <button
              className={`filtro-menu-lateral ${filtro === "clientes" ? "ativo" : ""}`}
              onClick={() => handleMenuClick("clientes", "Clientes", "clientes")}
            >
              <img src={icones.clientes} alt="" /> Clientes
            </button>
          </li>
          <li>
            <button
              className={`filtro-menu-lateral ${filtro === "servicos" ? "ativo" : ""}`}
              onClick={() => handleMenuClick("servicos", "Serviços", "servicos")}
            >
              <img src={icones.servicos} alt="" /> Serviços
            </button>
          </li>
          <li>
            <button
              className={`filtro-menu-lateral ${filtro === "configuracoes" ? "ativo" : ""}`}
              onClick={() => handleMenuClick("configuracoes", "Configurações", "configuracoes")}
            >
              <img src={icones.configuracoes} alt="" /> Configurações
            </button>
          </li>
        </ul>
      </section>

      <div className="minhas-tarefas">
        <main>
          <Header titulo={titulo} />
          {paginas[pagina] || <p>Página não encontrada</p>}
        </main>
      </div>
    </PagBase>
  );
}

export default PaginaBase;
