import React from 'react';
import styled from 'styled-components';

const Footer = styled.footer`
  display: flex;
  justify-content: space-between;
  margin-top: 20px;
`;

const MetricCard = styled.div`
  flex: 1;
  text-align: center;
  padding: 10px;
  border: 1px solid #ddd;
  border-radius: 4px;
  margin-right: 10px;
`;

const MetricTitle = styled.h3`
  margin: 0;
`;

const MetricValue = styled.span`
  font-size: 24px;
  font-weight: bold;
`;

const MetricChange = styled.small`
  color: ${props => (props.color === 'green' ? '#28a745' : '#dc3545')};
`;

function MetricsFooter() {
  return (
    <Footer>
      <MetricCard>
        <MetricTitle>Consultas Hoje</MetricTitle>
        <MetricValue>12</MetricValue>
        <MetricChange color="green">+15% vs ontem</MetricChange>
      </MetricCard>
      <MetricCard>
        <MetricTitle>Taxa de Ocupação</MetricTitle>
        <MetricValue>85%</MetricValue>
        <MetricChange color="green">+5% vs mês passado</MetricChange>
      </MetricCard>
      <MetricCard>
        <MetricTitle>Receita do Dia</MetricTitle>
        <MetricValue>R$ 2.150</MetricValue>
        <MetricChange color="green">+12% vs anterior</MetricChange>
      </MetricCard>
      <MetricCard>
        <MetricTitle>Cancelamentos</MetricTitle>
        <MetricValue>2</MetricValue>
        <MetricChange color="red">+1 vs ontem</MetricChange>
      </MetricCard>
    </Footer>
  );
}

export default MetricsFooter;
