import React from 'react';
import styled from 'styled-components';

const Grid = styled.table`
  width: 100%;
  border-collapse: collapse;
`;

const TableHead = styled.thead`
  background: #f8f9fa;
`;

const TableRow = styled.tr`
  & + tr {
    border-top: 1px solid #dee2e6;
  }
`;

const TableCell = styled.td`
  padding: 10px;
  border: 1px solid #ddd;
  text-align: center;

  &.disponivel {
    background: #f0f8ff;
  }

  &.ocupado {
    background: #bce8f1;
  }

  &.cancelado {
    background: #f8d7da;
  }
`;

function GridHorarios() {
  const horarios = [
    '08:00', '09:00', '10:00', '11:00', '12:00', '13:00',
    '14:00', '15:00', '16:00', '17:00', '18:00', '19:00'
  ];

  const medicos = ['Dr. Silva', 'Dr. Costa', 'Dra. Lima', 'Dr. Rocha'];

  return (
    <Grid>
      <TableHead>
        <TableRow>
          <TableCell>Horário</TableCell>
          {medicos.map((medico) => (
            <TableCell key={medico}>{medico}</TableCell>
          ))}
        </TableRow>
      </TableHead>
      <tbody>
        {horarios.map((hora) => (
          <TableRow key={hora}>
            <TableCell>{hora}</TableCell>
            {medicos.map((medico) => (
              <TableCell key={medico} className="disponivel">
                Disponível
              </TableCell>
            ))}
          </TableRow>
        ))}
      </tbody>
    </Grid>
  );
}

export default GridHorarios;
