import { useState } from "react";
import styled from 'styled-components';
import Header from "../../components/Header";
import DashBoard from "../Dashboard";
import Clientes from "../Clientes";
import img from '../../assets/imagens/logo.png';

const PagBase = styled.section`
  display: flex;

  .menu-lateral {
    height: 100vh;
    width: 20vw;
  }

  .menu-lateral ul {
    padding: 20px;
    border-top: 1px solid black;
    list-style: none;
  }

  .menu-lateral h2 {
    padding-left: 20px;
    margin-bottom: 22px;
  }

  .minhas-tarefas {
    width: 100vw;
    border-left: 1px solid black;
  }

  .filtro-menu-lateral {
    background: none;
    border: none;
    padding: 8px 16px;
    cursor: pointer;
    font-weight: bold;
    color: #888;
    border-bottom: 2px solid transparent;
    transition: 0.3s;
  }

  .filtro-menu-lateral:hover {
    color: #000;
  }

  .filtro-menu-lateral.ativo {
    color: #000;
    background-color: #f0f0f0;
    border-radius: 5px;
  }

  ul li img {
    width: 25px;
    margin-right: 15px;
    margin-bottom: -7px;
  }

  h2 {
    margin-left: 16px;
  }

  h2 img {
    width: 200px;
    margin-left: 15px;
    margin-bottom: -20px;
  }

  h1 {
    font-size: 35px;
  }
`;

function PaginaBase() {
  const [filtro, setFiltro] = useState("dashboard");
  const [pagina, setPagina] = useState("dashboard");
  const [titulo, setTitulo] = useState("Dashboard");

  const paginas = {
    dashboard: <DashBoard setPagina={setPagina} setTitulo={setTitulo} setFiltro={setFiltro} />,
    clientes: <Clientes />,
    agenda: <Agenda/>
  };

  return (
    <PagBase>
      <section className="menu-lateral">
        <h2><img src={img} alt="logo" /></h2>
        <ul>
          <li
            className={`filtro-menu-lateral ${filtro === "dashboard" ? "ativo" : ""}`}
            onClick={() => {
              setTitulo("Dashboard");
              setFiltro("dashboard");
              setPagina("dashboard");
            }}
          >
            DashBoard
          </li>
          <li
            className={`filtro-menu-lateral ${filtro === "agenda" ? "ativo" : ""}`}
            onClick={() => {
              setTitulo("Agenda");
              setFiltro("agenda");
              // Aqui você poderia adicionar o componente da Agenda quando estiver pronto
            }}
          >
            Agenda
          </li>
          <li
            className={`filtro-menu-lateral ${filtro === "clientes" ? "ativo" : ""}`}
            onClick={() => {
              setTitulo("Clientes");
              setFiltro("clientes");
              setPagina("clientes");
            }}
          >
            Clientes
          </li>
          <li
            className={`filtro-menu-lateral ${filtro === "servicos" ? "ativo" : ""}`}
            onClick={() => {
              setTitulo("Serviços");
              setFiltro("servicos");
              // Adicionar lógica futura para "Serviços"
            }}
          >
            Serviços
          </li>
          <li
            className={`filtro-menu-lateral ${filtro === "configuracoes" ? "ativo" : ""}`}
            onClick={() => {
              setTitulo("Configurações");
              setFiltro("configuracoes");
              // Adicionar lógica futura para "Configurações"
            }}
          >
            Configurações
          </li>
        </ul>
      </section>
      <div className='minhas-tarefas'>
        <main>
          <Header
            titulo={titulo}
            setPagina={setPagina}
            setTitulo={setTitulo}
            setFiltro={setFiltro}
          />
          {paginas[pagina]}
        </main>
      </div>
    </PagBase>
  );
}

export default PaginaBase;
